import { useState, useEffect, useRef } from 'react'
import { Link, useLocation } from 'react-router-dom'
import { Menu, X, Smartphone, Globe, ChevronDown, Check, Languages, Home, Settings, Calendar, FileText } from 'lucide-react'
import { useLanguage } from '../i18n/LanguageContext'

function Header() {
  const { language, setLanguage, t, languages, currentLanguage } = useLanguage()
  const [menuOpen, setMenuOpen] = useState(false)
  const [deferredPrompt, setDeferredPrompt] = useState(null)
  const [isInstalled, setIsInstalled] = useState(false)
  const [isIOS, setIsIOS] = useState(false)
  const [showIOSModal, setShowIOSModal] = useState(false)
  const [showLangDropdown, setShowLangDropdown] = useState(false)
  const [showAndroidModal, setShowAndroidModal] = useState(false)
  const langDropdownRef = useRef(null)
  const location = useLocation()
  const isRTL = language === 'ar' || language === 'ur' || language === 'fa'

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (langDropdownRef.current && !langDropdownRef.current.contains(event.target)) {
        setShowLangDropdown(false)
      }
    }
    document.addEventListener('mousedown', handleClickOutside)
    return () => document.removeEventListener('mousedown', handleClickOutside)
  }, [])

  useEffect(() => {
    const isIOSDevice = /iPad|iPhone|iPod/.test(navigator.userAgent) && !window.MSStream
    const isInStandaloneMode = window.matchMedia('(display-mode: standalone)').matches || window.navigator.standalone

    if (isInStandaloneMode) {
      setIsInstalled(true)
      return
    }

    if (isIOSDevice) {
      setIsIOS(true)
    }

    const handler = (e) => {
      e.preventDefault()
      setDeferredPrompt(e)
    }

    const installedHandler = () => {
      setIsInstalled(true)
      setDeferredPrompt(null)
    }

    window.addEventListener('beforeinstallprompt', handler)
    window.addEventListener('appinstalled', installedHandler)

    return () => {
      window.removeEventListener('beforeinstallprompt', handler)
      window.removeEventListener('appinstalled', installedHandler)
    }
  }, [])

  const handleInstall = async () => {
    setMenuOpen(false)
    if (isIOS) {
      setShowIOSModal(true)
      return
    }

    if (deferredPrompt) {
      deferredPrompt.prompt()
      const { outcome } = await deferredPrompt.userChoice
      if (outcome === 'accepted') {
        setIsInstalled(true)
      }
      setDeferredPrompt(null)
    } else {
      setShowAndroidModal(true)
    }
  }

  const navItems = [
    { path: '/', label: t.nav.home, icon: <Home size={18} /> },
    { path: '/services', label: t.nav.services, icon: <Settings size={18} /> },
    { path: '/booking', label: t.nav.booking, icon: <Calendar size={18} /> },
    { path: '/report', label: t.nav.report, icon: <FileText size={18} /> },
  ]

  return (
    <>
      <header className="header-pro">
        <div className="header-top">
          <div className="container">
            <Link to="/" className="logo-pro">
              <div className="logo-image-container">
                <img src="/images/logo.png" alt="High Safety Logo" />
              </div>
              <div className="logo-text-container">
                <h1>{language === 'ar' ? 'Ø§Ù„Ø£Ù…Ø§Ù† Ø§Ù„Ø¹Ø§Ù„ÙŠ Ø§Ù„Ø¯ÙˆÙ„ÙŠ' : 'High Safety International'}</h1>
                <span className="logo-subtitle">{language === 'ar' ? 'Ù„Ù„ÙØ­Øµ Ø§Ù„ÙÙ†ÙŠ Ù„Ù„Ø³ÙŠØ§Ø±Ø§Øª' : 'Technical Car Inspection'}</span>
              </div>
            </Link>

            <div className="header-actions">
              <div className="language-switcher-pro" ref={langDropdownRef}>
                <button 
                  className="lang-switcher-btn-pro"
                  onClick={() => setShowLangDropdown(!showLangDropdown)}
                >
                  <span className="lang-flag">{currentLanguage?.flag}</span>
                  <span className="lang-code">{currentLanguage?.code?.toUpperCase()}</span>
                  <ChevronDown 
                    size={14} 
                    className={`lang-chevron ${showLangDropdown ? 'rotated' : ''}`}
                  />
                </button>

                {showLangDropdown && (
                  <>
                    <div 
                      className="lang-dropdown-overlay" 
                      onClick={() => setShowLangDropdown(false)}
                    />
                    <div className="lang-dropdown-pro">
                      <div className="lang-dropdown-header">
                        <Globe size={16} />
                        <span>{language === 'ar' ? 'Ø§Ø®ØªØ± Ø§Ù„Ù„ØºØ©' : 'Select Language'}</span>
                      </div>
                      <div className="lang-dropdown-grid">
                        {languages.map((lang) => (
                          <button
                            key={lang.code}
                            className={`lang-option ${language === lang.code ? 'active' : ''}`}
                            onClick={() => {
                              setLanguage(lang.code);
                              setShowLangDropdown(false);
                              setMenuOpen(false);
                            }}
                          >
                            <span className="lang-option-flag">{lang.flag}</span>
                            <span className="lang-option-name">{lang.name}</span>
                            {language === lang.code && (
                              <Check size={14} className="lang-check" />
                            )}
                          </button>
                        ))}
                      </div>
                    </div>
                  </>
                )}
              </div>

              {!isInstalled && (
                <button className="install-btn-pro" onClick={handleInstall}>
                  <Smartphone size={16} />
                  <span>{language === 'ar' ? 'ØªØ­Ù…ÙŠÙ„' : 'Install'}</span>
                </button>
              )}

              <button 
                className="mobile-menu-btn-pro"
                onClick={() => setMenuOpen(!menuOpen)}
              >
                {menuOpen ? <X size={24} /> : <Menu size={24} />}
              </button>
            </div>
          </div>
        </div>

        <nav className={`nav-pro ${menuOpen ? 'nav-open' : ''}`}>
          <div className="container">
            <div className="nav-items">
              {navItems.map((item) => (
                <Link 
                  key={item.path}
                  to={item.path} 
                  className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
                  onClick={() => setMenuOpen(false)}
                >
                  {item.icon}
                  <span>{item.label}</span>
                </Link>
              ))}
            </div>
          </div>
        </nav>
      </header>

      <style>{`
        .header-pro {
          position: sticky;
          top: 0;
          z-index: 1000;
          background: linear-gradient(180deg, #0a0a14 0%, #0d0d1a 100%);
          box-shadow: 0 4px 30px rgba(0, 0, 0, 0.5);
        }

        .header-top {
          padding: 15px 0;
          border-bottom: 1px solid rgba(212, 168, 83, 0.15);
        }

        .header-top .container {
          display: flex;
          justify-content: space-between;
          align-items: center;
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
        }

        .logo-pro {
          display: flex;
          align-items: center;
          gap: 15px;
          text-decoration: none;
          color: white;
        }

        .logo-image-container {
          width: 60px;
          height: 60px;
          border-radius: 50%;
          overflow: hidden;
          border: 3px solid #D4A853;
          box-shadow: 0 4px 20px rgba(212, 168, 83, 0.4);
          flex-shrink: 0;
        }

        .logo-image-container img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .logo-text-container {
          display: flex;
          flex-direction: column;
        }

        .logo-text-container h1 {
          font-size: 1.4rem;
          font-weight: 800;
          color: #D4A853;
          margin: 0;
          text-shadow: 0 0 30px rgba(212, 168, 83, 0.3);
          letter-spacing: 0.5px;
        }

        .logo-subtitle {
          font-size: 0.85rem;
          color: rgba(255, 255, 255, 0.7);
          margin-top: 2px;
        }

        .header-actions {
          display: flex;
          align-items: center;
          gap: 12px;
        }

        .lang-switcher-btn-pro {
          display: flex;
          align-items: center;
          gap: 8px;
          background: rgba(212, 168, 83, 0.15);
          border: 1px solid rgba(212, 168, 83, 0.3);
