/* Language Switcher Styles */
.language-switcher {
  position: relative;
}

.language-switcher-btn:hover {
  background: rgba(255,255,255,0.2) !important;
  border-color: #C89D2A !important;
}

@keyframes fadeInDown {
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Interactive Report Page Styles */
.interactive-report-page {
  background: #f8fafb;
}

.interactive-hero {
  background: linear-gradient(135deg, var(--primary) 0%, #0d2847 100%);
  color: white;
  padding: 60px 20px;
  text-align: center;
}

.interactive-header h1 {
  font-size: 2.5rem;
  margin-bottom: 12px;
  font-weight: 800;
}

.interactive-header p {
  font-size: 1.1rem;
  opacity: 0.95;
}

.interactive-content {
  padding: 50px 20px;
}

.interactive-grid {
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 30px;
  align-items: start;
}

.viewer-section {
  background: white;
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.08);
  padding: 20px;
}

.viewer-header h2 {
  color: var(--primary);
  font-size: 1.4rem;
  margin-bottom: 8px;
}

.viewer-hint {
  color: #666;
  font-size: 0.9rem;
  margin-bottom: 20px;
}

.viewer-container {
  width: 100%;
  height: 500px;
  background: linear-gradient(135deg, #f0f4f8 0%, #e8eef5 100%);
  border-radius: 12px;
  border: 2px solid #e8eef5;
}

.defects-panel {
  background: white;
  border-radius: 16px;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.08);
  display: flex;
  flex-direction: column;
  height: 100%;
}

.panel-header {
  padding: 20px;
  border-bottom: 2px solid #f0f4f8;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.panel-header h2 {
  color: var(--primary);
  font-size: 1.2rem;
  margin: 0;
}

.defect-count {
  background: linear-gradient(135deg, var(--secondary), #d4af37);
  color: white;
  font-weight: 700;
  padding: 6px 14px;
  border-radius: 20px;
  font-size: 0.9rem;
}

.defects-list {
  flex: 1;
  overflow-y: auto;
  padding: 15px;
}

.defect-item {
  padding: 15px;
  border: 2px solid #f0f4f8;
  border-radius: 10px;
  margin-bottom: 10px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.defect-item:hover {
  border-color: var(--secondary);
  background: rgba(200, 157, 42, 0.05);
}

