        }

        .step-header {
          margin-bottom: 20px;
        }

        .back-btn {
          display: inline-flex;
          align-items: center;
          gap: 8px;
          background: transparent;
          border: none;
          color: #0B1F3A;
          cursor: pointer;
          font-size: 1rem;
          font-weight: 500;
          padding: 10px 0;
          transition: color 0.3s ease;
        }

        .back-btn:hover {
          color: #C89D2A;
        }

        .category-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 20px;
        }

        .category-card {
          background: #f8f9fa;
          border: 2px solid transparent;
          border-radius: 16px;
          padding: 30px 20px;
          text-align: center;
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .category-card:hover {
          border-color: #C89D2A;
          transform: translateY(-5px);
          box-shadow: 0 10px 30px rgba(200, 157, 42, 0.15);
        }

        .category-card.selected {
          border-color: #C89D2A;
          background: rgba(200, 157, 42, 0.08);
        }

        .category-icon {
          margin-bottom: 15px;
          display: flex;
          justify-content: center;
          align-items: center;
        }
        .price-prefix {
          color: #C89D2A;
          font-weight: 700;
          font-size: 1rem;
          margin-right: 2px;
        }

        .category-card h3 {
          margin: 0 0 8px 0;
          color: #0B1F3A;
          font-size: 1.1rem;
        }

        .category-card p {
          margin: 0 0 15px 0;
          color: #666;
          font-size: 0.85rem;
        }

        .category-price-range {
          background: #0B1F3A;
