        }

        .payment-form-section h2 {
          text-align: left;
          font-size: 1.5rem;
          margin-bottom: 25px;
        }

        [dir="rtl"] .payment-form-section h2 {
          text-align: right;
        }

        .error-alert {
          background: #fee2e2;
          color: #dc2626;
          padding: 15px 20px;
          border-radius: 12px;
          margin-bottom: 20px;
          font-weight: 500;
        }

        .payment-form-new {
          display: flex;
          flex-direction: column;
          gap: 20px;
        }

        .form-field label {
          display: flex;
          align-items: center;
          gap: 8px;
          font-weight: 600;
          color: #0B1F3A;
          margin-bottom: 8px;
          font-size: 0.95rem;
        }

        .form-field input {
          width: 100%;
          padding: 15px 18px;
          border: 2px solid #e8e8e8;
          border-radius: 12px;
          font-size: 1rem;
          transition: all 0.3s ease;
          background: #f8f9fa;
        }

        .form-field input:focus {
          outline: none;
          border-color: #C89D2A;
          background: white;
        }

        .submit-payment-btn {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 12px;
          background: linear-gradient(135deg, #C89D2A 0%, #a8822a 100%);
          color: white;
          border: none;
          padding: 18px 30px;
          border-radius: 12px;
          font-size: 1.1rem;
          font-weight: 700;
          cursor: pointer;
          transition: all 0.3s ease;
          margin-top: 10px;
        }

        .submit-payment-btn:hover:not(:disabled) {
          transform: translateY(-2px);
          box-shadow: 0 10px 25px rgba(200, 157, 42, 0.3);
        }

        .submit-payment-btn:disabled {
          opacity: 0.7;
          cursor: not-allowed;
        }

        .btn-amount {
          background: rgba(255, 255, 255, 0.2);
          padding: 5px 12px;
          border-radius: 8px;
          font-size: 0.95rem;
        }

        .spinner {
          width: 20px;
          height: 20px;
          border: 2px solid rgba(255, 255, 255, 0.3);
          border-top-color: white;
          border-radius: 50%;
          animation: spin 0.8s linear infinite;
        }

        @keyframes spin {
          to { transform: rotate(360deg); }
        }

        .redirect-note {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 8px;
          color: #666;
          font-size: 0.85rem;
          margin: 0;
        }

        .order-summary {
          background: #f8f9fa;
          border-radius: 16px;
          padding: 25px;
          height: fit-content;
        }

        .order-summary h3 {
          margin: 0 0 20px 0;
          color: #0B1F3A;
          font-size: 1.2rem;
          padding-bottom: 15px;
          border-bottom: 2px solid #e8e8e8;
        }

        .summary-item {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 12px 0;
        }

        .summary-label {
          color: #666;
          font-size: 0.9rem;
        }

        .summary-value {
          color: #0B1F3A;
          font-weight: 600;
          font-size: 0.95rem;
        }

        .summary-divider {
          height: 2px;
          background: #e8e8e8;
          margin: 10px 0;
        }

        .summary-total {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 15px 0;
          font-size: 1.1rem;
          font-weight: 700;
        }

        .total-value {
          color: #C89D2A;
          font-size: 1.4rem;
        }

        .security-features {
          background: white;
          border-radius: 12px;
          padding: 15px;
          margin-top: 20px;
        }

        .security-item {
          display: flex;
          align-items: center;
          gap: 10px;
          padding: 8px 0;
          color: #34A853;
          font-size: 0.85rem;
        }

        .accepted-cards-new {
          margin-top: 20px;
          text-align: center;
        }

        .accepted-cards-new span {
          display: block;
          color: #666;
          font-size: 0.8rem;
          margin-bottom: 10px;
        }

        .cards-logos {
          display: flex;
          justify-content: center;
          gap: 10px;
        }

        .card-logo {
          padding: 8px 15px;
          border-radius: 6px;
          font-weight: 700;
          font-size: 0.8rem;
        }

        .card-logo.visa {
          background: #1A1F71;
          color: white;
        }

        .card-logo.mastercard {
          background: linear-gradient(135deg, #EB001B 0%, #F79E1B 100%);
          color: white;
        }

        @media (max-width: 600px) {
          .payment-hero-new {
            padding: 60px 16px 50px;
          }

          .payment-hero-new h1 {
            font-size: 1.8rem;
          }

          .payment-progress {
            padding: 15px 20px;
          }

          .step-label {
            display: none;
          }

          .step-connector {
            width: 20px;
          }

          .payment-wizard-content {
            padding: 25px 20px;
          }

          .category-grid,
          .service-grid {
            grid-template-columns: repeat(2, 1fr);
            gap: 12px;
          }

          .category-card,
          .service-card {
            padding: 20px 15px;
          }

          .category-icon svg {
            width: 60px !important;
            height: 30px !important;
          }

          .service-icon {
            font-size: 2rem;
          }
        }
      `}</style>
    </div>
  )
}

export default Payment

