/* Professional Design System */
/* Color Scheme: Luxury Dark Garage Theme */

:root {
  --primary: #0a0a14;
  --primary-light: #12121f;
  --secondary: #D4A853;
  --secondary-glow: #FF8C00;
  --accent-blue: #1a3a5c;
  --accent-green: #34A853;
  --accent-red: #EA4335;
  --text-dark: #ffffff;
  --text-light: #a0a0b0;
  --text-body: #c0c0d0;
  --bg-light: #0a0a14;
  --bg-card: rgba(20, 20, 35, 0.8);
  --border-glow: rgba(212, 168, 83, 0.3);
  --glow-orange: #FF8C00;
}

* {
  box-sizing: border-box;
}

body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  margin: 0;
  padding: 0;
  background: var(--primary);
  color: var(--text-body);
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

/* ============================================
   HEADER STYLES - Luxury Dark Theme
   ============================================ */
.header {
  background: linear-gradient(180deg, #0a0a14 0%, #0d0d1a 100%);
  padding: 15px 0;
  position: sticky;
  top: 0;
  z-index: 1000;
  box-shadow: 0 4px 30px rgba(0, 0, 0, 0.5);
  border-bottom: 1px solid rgba(212, 168, 83, 0.15);
}

.header .container {
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 15px;
}

.header .nav {
  display: flex;
  align-items: center;
  gap: 12px;
  flex-wrap: wrap;
}

.header .nav a {
  color: #ffffff;
  text-decoration: none;
  font-size: 0.9rem;
  font-weight: 500;
  padding: 10px 20px;
  border-radius: 25px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  background: transparent;
  transition: all 0.3s ease;
  white-space: nowrap;
}

.header .nav a:hover {
  background: rgba(212, 168, 83, 0.15);
  border-color: var(--secondary);
  color: var(--secondary);
}

.mobile-menu-btn {
  display: none !important;
}

@media (max-width: 900px) {
  .mobile-menu-btn {
    display: none !important;
  }

  .header {
    padding: 10px 0 !important;
    box-shadow: 0 4px 25px rgba(0,0,0,0.25) !important;
  }

  .header .container {
    flex-direction: column !important;
    gap: 12px !important;
    padding: 8px 12px !important;
    align-items: center !important;
  }

  .header .logo {
    gap: 10px !important;
  }

  .header .logo img {
    width: 42px !important;
    height: 42px !important;
  }

  .header .logo h1 {
    font-size: 1rem !important;
  }

  .header .nav {
    display: flex !important;
    flex-wrap: nowrap !important;
    overflow-x: auto !important;
    gap: 10px !important;
    width: 100% !important;
    padding: 8px 4px 10px !important;
    -webkit-overflow-scrolling: touch;
    scrollbar-width: none;
    -ms-overflow-style: none;
    justify-content: flex-start !important;
    background: linear-gradient(135deg, rgba(255,255,255,0.08), rgba(255,255,255,0.03));
    border-radius: 15px;
    margin: 0 -4px;
  }

  .header .nav::-webkit-scrollbar {
    display: none;
  }

  .header .nav > a,
  .header .nav > button,
  .header .nav > div {
    flex-shrink: 0 !important;
  }

  .header .nav a {
    padding: 10px 16px !important;
    font-size: 0.85rem !important;
    white-space: nowrap !important;
    border-radius: 12px !important;
    background: rgba(255, 255, 255, 0.12) !important;
    border: 1px solid rgba(255,255,255,0.15) !important;
    font-weight: 600 !important;
    transition: all 0.25s ease !important;
    box-shadow: 0 2px 8px rgba(0,0,0,0.1) !important;
  }

  .header .nav a:hover,
  .header .nav a:active {
    background: linear-gradient(135deg, rgba(200, 157, 42, 0.35), rgba(200, 157, 42, 0.2)) !important;
    border-color: rgba(200, 157, 42, 0.5) !important;
    transform: scale(1.02) !important;
  }

  .header .nav button {
    padding: 10px 14px !important;
    font-size: 0.85rem !important;
    white-space: nowrap !important;
    border-radius: 12px !important;
    font-weight: 700 !important;
    box-shadow: 0 3px 12px rgba(0,0,0,0.15) !important;
  }

  .header .nav .language-switcher-pro {
    flex-shrink: 0 !important;
  }

  .header .nav .lang-switcher-btn-pro {
    padding: 8px 14px !important;
    font-size: 0.85rem !important;
    border-radius: 14px !important;
    gap: 8px !important;
  }

  .header .nav .lang-btn-icon {
    width: 30px !important;
    height: 30px !important;
  }

  .header .nav .lang-btn-icon svg {
    width: 15px !important;
    height: 15px !important;
  }

  .header .nav .lang-flag {
    font-size: 1.2rem !important;
  }

  .header .nav .lang-code {
    font-size: 0.8rem !important;
    font-weight: 700 !important;
  }

  .header .nav .lang-chevron {
    width: 14px !important;
    height: 14px !important;
  }
}

/* ============================================
   HOME PAGE - HERO SECTION - Luxury Dark Theme
   ============================================ */
.home-page {
  background: var(--primary);
}

.hero-modern {
  background: linear-gradient(135deg, #0a0a14 0%, #101020 50%, #0a0a14 100%);
  color: white;
  padding: 100px 20px;
  position: relative;
  overflow: hidden;
}

.hero-modern::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    radial-gradient(ellipse at 30% 20%, rgba(212, 168, 83, 0.08) 0%, transparent 50%),
    radial-gradient(ellipse at 70% 80%, rgba(255, 140, 0, 0.05) 0%, transparent 50%);
