  align-items: center;
  justify-content: space-between;
  gap: 50px;
  position: relative;
  z-index: 1;
}

.hero-modern .hero-content {
  flex: 1;
  max-width: 650px;
}

.hero-badge {
  display: inline-flex;
  align-items: center;
  gap: 10px;
  background: rgba(212, 168, 83, 0.15);
  border: 1px solid rgba(212, 168, 83, 0.4);
  color: var(--secondary);
  padding: 10px 24px;
  border-radius: 30px;
  font-size: 0.9rem;
  font-weight: 600;
  margin-bottom: 25px;
  backdrop-filter: blur(10px);
}

.hero-modern h1 {
  font-size: 3rem;
  font-weight: 800;
  margin: 0 0 15px 0;
  line-height: 1.2;
  color: var(--secondary);
  text-shadow: 0 0 40px rgba(212, 168, 83, 0.3);
  letter-spacing: 1px;
}

.hero-modern h2 {
  font-size: 1.6rem;
  font-weight: 600;
  margin: 0 0 20px 0;
  color: #ffffff;
  text-shadow: 0 2px 10px rgba(0, 0, 0, 0.5);
}

.hero-subtitle {
  font-size: 1.1rem;
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.8;
  margin-bottom: 30px;
}

.hero-actions {
  display: flex;
  gap: 20px;
  flex-wrap: wrap;
}

.btn-hero-primary {
  display: inline-flex;
  align-items: center;
  gap: 10px;
  background: linear-gradient(135deg, var(--secondary), #e8b84a);
  color: #0a0a14;
  padding: 16px 32px;
  border-radius: 30px;
  font-weight: 700;
  font-size: 1rem;
  text-decoration: none;
  transition: all 0.3s ease;
  box-shadow: 0 4px 25px rgba(212, 168, 83, 0.4), 0 0 40px rgba(212, 168, 83, 0.2);
}

.btn-hero-primary:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 40px rgba(212, 168, 83, 0.6), 0 0 60px rgba(212, 168, 83, 0.3);
}

.btn-hero-secondary {
  display: inline-flex;
  align-items: center;
  gap: 10px;
  background: rgba(255,255,255,0.15);
  color: white;
  padding: 14px 28px;
  border-radius: 30px;
  font-weight: 600;
  font-size: 1rem;
  text-decoration: none;
  border: 2px solid rgba(255,255,255,0.3);
  transition: all 0.3s ease;
}

.btn-hero-secondary:hover {
  background: rgba(255,255,255,0.25);
  border-color: rgba(255,255,255,0.5);
}

.hero-image {
  flex-shrink: 0;
}

.hero-logo {
  width: 200px;
  height: 200px;
  object-fit: contain;
  filter: drop-shadow(0 10px 30px rgba(0,0,0,0.3));
}

/* ============================================
   STATS BAR - Luxury Dark Theme
   ============================================ */
.stats-bar {
  background: linear-gradient(180deg, #0d0d1a 0%, #0a0a14 100%);
  padding: 40px 0;
  box-shadow: 0 10px 40px rgba(0,0,0,0.3);
  position: relative;
  z-index: 10;
  border-top: 1px solid rgba(212, 168, 83, 0.1);
  border-bottom: 1px solid rgba(212, 168, 83, 0.1);
}

.stats-grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 25px;
}

.stat-item {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 18px;
  padding: 20px;
  border-radius: 16px;
  background: rgba(20, 20, 35, 0.6);
  border: 1px solid rgba(212, 168, 83, 0.15);
  transition: all 0.3s ease;
}

.stat-item:hover {
  background: rgba(212, 168, 83, 0.1);
  border-color: rgba(212, 168, 83, 0.3);
  transform: translateY(-3px);
}

.stat-icon {
  width: 55px;
  height: 55px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, var(--secondary), #e8b84a);
  color: #0a0a14;
  border-radius: 14px;
  box-shadow: 0 4px 20px rgba(212, 168, 83, 0.3);
}

.stat-info {
  display: flex;
  flex-direction: column;
}

.stat-number {
  font-size: 1.6rem;
  font-weight: 800;
  color: #ffffff;
}

.stat-label {
  font-size: 0.9rem;
  color: var(--text-light);
}

/* ============================================
   SERVICES SECTION - Luxury Dark Theme
   ============================================ */
.services-section {
  padding: 80px 20px;
  background: linear-gradient(180deg, #0a0a14 0%, #0d0d1a 100%);
}

.section-header {
  text-align: center;
  margin-bottom: 50px;
}

.section-badge {
  display: inline-block;
  background: rgba(212, 168, 83, 0.15);
  border: 1px solid rgba(212, 168, 83, 0.4);
  color: var(--secondary);
  padding: 10px 24px;
  border-radius: 30px;
  font-size: 0.9rem;
  font-weight: 600;
  margin-bottom: 20px;
}

.section-header h2 {
  font-size: 2.2rem;
  color: #ffffff;
  margin: 0 0 15px 0;
}

.section-header p {
  color: var(--text-light);
  font-size: 1.05rem;
  margin: 0;
}

.section-header.light h2 {
  color: white;
}

.section-header.light p {
  color: rgba(255,255,255,0.8);
}

.services-grid {
  display: flex;
  overflow-x: auto;
  gap: 25px;
  padding-bottom: 20px;
  scroll-snap-type: x mandatory;
  -webkit-overflow-scrolling: touch;
  scrollbar-width: none;
  -ms-overflow-style: none;
}

.services-grid::-webkit-scrollbar {
  display: none;
}

.services-grid .service-card {
  flex: 0 0 320px;
  scroll-snap-align: start;
}

.service-card {
  background: rgba(20, 20, 35, 0.8);
  border-radius: 20px;
  padding: 30px;
  box-shadow: 0 10px 40px rgba(0,0,0,0.3);
  transition: all 0.3s ease;
  border: 1px solid rgba(212, 168, 83, 0.15);
  border-top: 3px solid var(--card-color, var(--secondary));
}

.service-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 20px 50px rgba(0,0,0,0.4), 0 0 30px rgba(212, 168, 83, 0.1);
  border-color: rgba(212, 168, 83, 0.3);
}

.service-icon {
  width: 60px;
  height: 60px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 14px;
  color: white;
  margin-bottom: 20px;
}

.service-content h3 {
  font-size: 1.2rem;
  color: #ffffff;
  margin: 0 0 5px 0;
}

.service-title-en {
  display: block;
  font-size: 0.85rem;
  color: var(--text-light);
  margin-bottom: 10px;
}

.service-content p {
  color: var(--text-light);
  font-size: 0.9rem;
  margin-bottom: 15px;
}

.service-features {
  list-style: none;
  padding: 0;
  margin: 0 0 20px 0;
}

.service-features li {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 0.85rem;
  color: var(--text-dark);
  padding: 6px 0;
}

.service-features li svg {
  color: var(--accent-green);
}

.service-footer {
  padding-top: 15px;
  border-top: 1px solid rgba(212, 168, 83, 0.2);
}

.service-btn {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  background: linear-gradient(135deg, var(--secondary), #d4af37);
  color: white;
  padding: 12px 28px;
  border-radius: 30px;
  font-weight: 700;
  font-size: 0.95rem;
  text-decoration: none;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(200, 157, 42, 0.3);
}

.service-btn:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 25px rgba(200, 157, 42, 0.4);
  background: linear-gradient(135deg, #d4af37, #C89D2A);
}

.service-btn svg {
  transition: transform 0.3s ease;
}

.service-btn:hover svg {
  transform: translateX(3px);
}

[dir="rtl"] .service-btn:hover svg {
  transform: translateX(-3px);
}

/* ============================================
   WHY US SECTION - Luxury Dark Theme
   ============================================ */
.why-us-section {
  padding: 60px 20px;
  background: linear-gradient(180deg, #0d0d1a 0%, #0a0a14 100%);
}

.why-us-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 25px;
}

.why-us-card {
  background: rgba(20, 20, 35, 0.8);
  backdrop-filter: blur(10px);
  border-radius: 16px;
  padding: 30px;
  text-align: center;
  border: 1px solid rgba(212, 168, 83, 0.15);
  transition: all 0.3s ease;
}

.why-us-card:hover {
  background: rgba(212, 168, 83, 0.1);
  border-color: rgba(212, 168, 83, 0.3);
  transform: translateY(-5px);
}

.why-us-icon {
  width: 70px;
  height: 70px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, var(--secondary), #d4af37);
  color: white;
  border-radius: 50%;
  margin: 0 auto 20px;
}

.why-us-card h3 {
  color: white;
  font-size: 1.1rem;
  margin: 0 0 10px 0;
}

.why-us-card p {
  color: rgba(255,255,255,0.8);
  font-size: 0.9rem;
  margin: 0;
}

/* ============================================
   GALLERY SECTION - Luxury Dark Theme
   ============================================ */
.gallery-section {
  padding: 60px 20px;
  background: linear-gradient(180deg, #0a0a14 0%, #0d0d1a 100%);
}

.gallery-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
}

.gallery-item {
  position: relative;
  border-radius: 16px;
  overflow: hidden;
  aspect-ratio: 16/10;
  box-shadow: 0 10px 40px rgba(0,0,0,0.4);
  border: 1px solid rgba(212, 168, 83, 0.15);
}

.gallery-item img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.5s ease;
}

.gallery-item:hover img {
  transform: scale(1.1);
}

.gallery-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(transparent, rgba(0,0,0,0.8));
  padding: 20px;
  color: white;
}

.gallery-overlay h3 {
  margin: 0 0 5px 0;
  font-size: 1rem;
}

.gallery-overlay p {
  margin: 0;
  font-size: 0.85rem;
  opacity: 0.8;
}

/* ============================================
   FACILITIES GRID - Luxury Dark Theme
   ============================================ */
.facilities-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
  margin-top: 30px;
}

.facility-card {
  background: rgba(20, 20, 35, 0.8);
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0,0,0,0.3);
  border: 1px solid rgba(212, 168, 83, 0.15);
  transition: all 0.3s ease;
}

.facility-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 20px 50px rgba(0,0,0,0.4);
  border-color: rgba(212, 168, 83, 0.3);
}

.facility-card img {
  width: 100%;
  height: 160px;
  object-fit: cover;
}

.facility-info {
  padding: 20px;
  display: flex;
  align-items: flex-start;
  gap: 15px;
}

.facility-info svg {
  color: var(--secondary);
  flex-shrink: 0;
}

.facility-info h4 {
  margin: 0 0 5px 0;
  font-size: 1rem;
  color: #ffffff;
}

.facility-info p {
  margin: 0;
  font-size: 0.85rem;
  color: var(--text-light);
}

/* ============================================
   CTA SECTION - Luxury Dark Theme
   ============================================ */
.cta-section,
.cta-section-home {
  padding: 60px 20px;
  background: linear-gradient(135deg, rgba(212, 168, 83, 0.15) 0%, rgba(20, 20, 35, 0.95) 50%, rgba(212, 168, 83, 0.1) 100%);
  border-top: 1px solid rgba(212, 168, 83, 0.3);
  border-bottom: 1px solid rgba(212, 168, 83, 0.3);
  text-align: center;
}

.cta-content {
  max-width: 600px;
  margin: 0 auto;
}

.cta-section h2,
.cta-section-home h2 {
  font-size: 2rem;
  color: var(--secondary);
  margin: 0 0 15px 0;
  text-shadow: 0 0 30px rgba(212, 168, 83, 0.3);
}

.cta-section p,
.cta-section-home p {
  color: var(--text-light);
  margin-bottom: 25px;
}

.cta-buttons {
  display: flex;
  gap: 15px;
  justify-content: center;
  flex-wrap: wrap;
}

.btn-cta-primary {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  background: var(--primary);
  color: white;
  padding: 14px 30px;
  border-radius: 30px;
  font-weight: 700;
  text-decoration: none;
  transition: all 0.3s ease;
}

.btn-cta-primary:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 30px rgba(11, 31, 58, 0.4);
}

.btn-cta-whatsapp {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  background: #25D366;
  color: white;
  padding: 14px 30px;
  border-radius: 30px;
  font-weight: 700;
  text-decoration: none;
  transition: all 0.3s ease;
}

.btn-cta-whatsapp:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 30px rgba(37, 211, 102, 0.4);
}

/* ============================================
   LOCATION SECTION - Luxury Dark Theme
   ============================================ */
.location-section {
  padding: 60px 20px;
  background: linear-gradient(180deg, #0d0d1a 0%, #0a0a14 100%);
}

.location-grid {
  display: grid;
  grid-template-columns: 1fr 1.5fr;
  gap: 30px;
  align-items: stretch;
  margin-top: 30px;
}

.location-card {
  background: rgba(20, 20, 35, 0.8);
  border-radius: 16px;
  padding: 30px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  border: 1px solid rgba(212, 168, 83, 0.15);
}

.location-card .location-info {
  display: flex;
  align-items: flex-start;
  gap: 15px;
  margin-bottom: 25px;
}

.location-icon {
  width: 60px;
  height: 60px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, var(--secondary), #e8b84a);
  color: #0a0a14;
  border-radius: 12px;
  flex-shrink: 0;
  box-shadow: 0 4px 20px rgba(212, 168, 83, 0.3);
}

.location-card h4 {
  margin: 0 0 8px 0;
  font-size: 1.1rem;
  color: #ffffff;
}

.location-card p {
  margin: 0;
  color: var(--text-light);
  font-size: 0.95rem;
  line-height: 1.5;
}

.location-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  background: linear-gradient(135deg, var(--secondary), #d4af37);
  color: var(--primary);
  padding: 14px 24px;
  border-radius: 30px;
  font-weight: 700;
  text-decoration: none;
  transition: all 0.3s ease;
}

.location-btn:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 25px rgba(200, 157, 42, 0.4);
}

.location-map {
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0,0,0,0.1);
  min-height: 300px;
}

.location-map iframe {
  width: 100%;
  height: 100%;
  min-height: 300px;
  border: none;
}

.location-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 40px;
  align-items: center;
}

.location-info h3 {
  font-size: 1.5rem;
  color: #ffffff;
  margin: 0 0 20px 0;
}

.location-info svg {
  color: var(--secondary);
}

.map-container {
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0,0,0,0.1);
}

.map-container iframe {
  width: 100%;
  height: 300px;
  border: none;
}

/* ============================================
   FOOTER - Luxury Dark Theme
   ============================================ */
.footer {
  background: linear-gradient(180deg, #0a0a14 0%, #050508 100%);
  color: white;
  padding: 40px 20px 20px;
  border-top: 1px solid rgba(212, 168, 83, 0.2);
}

.footer-content {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 30px;
  margin-bottom: 30px;
}

.footer-section h4 {
  color: var(--secondary);
  margin: 0 0 15px 0;
  font-size: 1rem;
  text-shadow: 0 0 20px rgba(212, 168, 83, 0.2);
}

.footer-section p,
.footer-section a {
  color: rgba(255,255,255,0.7);
  font-size: 0.9rem;
  margin-bottom: 10px;
  display: block;
  text-decoration: none;
}

.footer-section a:hover {
  color: var(--secondary);
}

.footer-bottom {
  text-align: center;
  padding-top: 20px;
  border-top: 1px solid rgba(212, 168, 83, 0.15);
  color: rgba(255,255,255,0.5);
  font-size: 0.85rem;
}

/* ============================================
   SIMPLE BOOKING PAGE
   ============================================ */
.simple-booking {
  background: var(--bg-light);
  min-height: 100vh;
  padding-bottom: 40px;
}

.booking-header-compact {
  background: linear-gradient(135deg, var(--primary) 0%, #1a3a5c 100%);
  color: white;
  padding: 30px 20px;
  text-align: center;
}

.booking-header-compact h1 {
  font-size: 1.6rem;
  margin: 0 0 5px 0;
  font-weight: 700;
}

.booking-header-compact p {
  opacity: 0.85;
  font-size: 0.9rem;
  margin: 0;
}

.booking-container {
  max-width: 600px;
  margin: -20px auto 0;
  padding: 0 15px;
}

@media (max-width: 640px) {
  .booking-container {
    max-width: 100%;
    padding: 0 10px;
    margin: -10px auto 0;
  }

  .step-content {
    padding: 15px 10px;
    border-radius: 8px;
  }

  .time-slots-grid {
    padding: 10px 5px;
    gap: 8px;
  }

  .time-slot {
    padding: 12px 8px;
    font-size: 0.9rem;
  }

  .steps-indicator {
    padding: 12px 10px;
    border-radius: 8px;
    margin-bottom: 15px;
  }
}

.steps-indicator {
  display: flex;
  justify-content: space-between;
  background: white;
  border-radius: 12px;
  padding: 15px 20px;
  box-shadow: 0 3px 15px rgba(0,0,0,0.08);
  margin-bottom: 20px;
}

.step-dot {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 5px;
  flex: 1;
}

.step-dot .dot {
  width: 28px;
  height: 28px;
  border-radius: 50%;
  background: #e0e0e0;
  color: #999;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.8rem;
  font-weight: 600;
  transition: all 0.3s;
}

.step-dot.active .dot {
  background: var(--primary);
  color: white;
}

.step-dot.current .dot {
  background: var(--secondary);
  color: var(--primary);
  box-shadow: 0 0 0 3px rgba(200, 157, 42, 0.3);
}

.step-dot .label {
  font-size: 0.7rem;
  color: #888;
}

.step-dot.active .label {
  color: var(--primary);
  font-weight: 500;
}

.step-content {
  background: white;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 3px 15px rgba(0,0,0,0.08);
  min-height: 300px;
}

.services-compact {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.service-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 15px;
  border: 2px solid #eee;
  border-radius: 10px;
  cursor: pointer;
  transition: all 0.2s;
  background: #fafafa;
}

.service-item:hover {
  background: white;
  border-color: #ddd;
}

.service-item.selected {
  background: white;
  box-shadow: 0 3px 12px rgba(0,0,0,0.08);
}

.service-item .service-icon {
  width: 40px;
  height: 40px;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  flex-shrink: 0;
}

.service-item .service-info {
  flex: 1;
  display: flex;
  flex-direction: column;
}

.service-item .service-info strong {
  font-size: 0.95rem;
  color: var(--primary);
}

.service-item .service-info small {
  font-size: 0.75rem;
  color: #888;
}

.service-item .service-price {
  font-size: 1.1rem;
  font-weight: 700;
  color: var(--secondary);
}

.service-item .service-price small {
  font-size: 0.7rem;
  color: #888;
}

.service-item .check-mark {
  margin-right: 5px;
}

.calendar-compact {
  text-align: center;
}

.month-nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 15px;
}

.month-nav button {
  background: #f5f5f5;
  border: none;
  width: 32px;
  height: 32px;
  border-radius: 8px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--primary);
}

.month-nav span {
  font-weight: 600;
  color: var(--primary);
}

.days-header {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 4px;
  margin-bottom: 8px;
}

.days-header span {
  font-size: 0.7rem;
  color: #888;
  text-align: center;
  padding: 5px 0;
}

.days-grid {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 4px;
}

.day-btn {
  aspect-ratio: 1;
  border: none;
  background: #f8f8f8;
  border-radius: 6px;
  font-size: 0.85rem;
  cursor: pointer;
  transition: all 0.2s;
  color: var(--text-dark);
}

.day-btn:hover:not(.disabled):not(.empty) {
  background: #e8f0ff;
}

.day-btn.selected {
  background: var(--primary);
  color: white;
}

.day-btn.disabled {
  color: #ccc;
  cursor: not-allowed;
}

.day-btn.empty {
  background: transparent;
  cursor: default;
}

.times-compact {
  text-align: center;
}

.selected-date-label {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  background: #f0f4f8;
  padding: 8px 15px;
  border-radius: 20px;
  font-size: 0.85rem;
  color: var(--primary);
  margin-bottom: 15px;
}

.times-grid {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 8px;
}

.time-btn {
  padding: 10px 5px;
  border: 1px solid #e0e0e0;
  background: white;
  border-radius: 8px;
  font-size: 0.85rem;
  cursor: pointer;
  transition: all 0.2s;
}

.time-btn:hover:not(.booked) {
  border-color: var(--primary);
  background: #f0f4ff;
}

.time-btn.selected {
  background: var(--primary);
  color: white;
  border-color: var(--primary);
}

.time-btn.booked {
  background: #f5f5f5;
  color: #bbb;
  cursor: not-allowed;
  text-decoration: line-through;
}

.form-compact .summary-bar {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-bottom: 20px;
  justify-content: center;
}

