  margin: 0 auto;
}

.car-instructions {
  text-align: center;
  color: #888;
  font-size: 0.9rem;
  margin: 0;
}

/* Defects Table Section */
.defects-table-section {
  padding: 40px 20px;
  background: white;
}

.section-header-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 15px;
  margin-bottom: 25px;
  max-width: 1000px;
  margin-left: auto;
  margin-right: auto;
}

.section-title {
  display: flex;
  align-items: center;
  gap: 10px;
}

.section-title h2 {
  margin: 0;
  color: var(--primary);
  font-size: 1.4rem;
}

.section-title svg {
  color: #EA4335;
}

.count-badge {
  background: #EA4335;
  color: white;
  padding: 4px 12px;
  border-radius: 15px;
  font-size: 0.85rem;
  font-weight: 600;
}

.download-pdf-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  background: linear-gradient(135deg, #34A853, #2d9048);
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 10px;
  font-weight: 600;
  font-size: 0.95rem;
  cursor: pointer;
  transition: all 0.3s;
}

.download-pdf-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 5px 20px rgba(52, 168, 83, 0.3);
}

.defects-table-wrapper {
  max-width: 1000px;
  margin: 0 auto;
  overflow-x: auto;
}

.defects-table {
  width: 100%;
  border-collapse: collapse;
  font-size: 0.9rem;
}

.defects-table th {
  background: var(--primary);
  color: white;
  padding: 14px 12px;
  text-align: right;
  font-weight: 600;
  white-space: nowrap;
}

.defects-table th:first-child {
  border-radius: 8px 0 0 0;
}

.defects-table th:last-child {
  border-radius: 0 8px 0 0;
}

.defects-table td {
  padding: 14px 12px;
  border-bottom: 1px solid #eee;
  vertical-align: middle;
}

.defects-table tr:hover {
  background: #f8fafb;
}

.defects-table tr.selected {
  background: rgba(200, 157, 42, 0.1);
}

.defects-table .severity-badge {
  display: inline-block;
  color: white;
  padding: 4px 10px;
  border-radius: 12px;
  font-size: 0.75rem;
  font-weight: 600;
}

.defects-table .desc-cell {
  max-width: 200px;
  color: #666;
}

.defects-table .cost-cell {
  color: var(--secondary);
  font-weight: 600;
  white-space: nowrap;
}

.defects-table .view-btn {
  background: #f0f4f8;
  border: none;
  padding: 8px;
  border-radius: 6px;
  cursor: pointer;
  color: var(--primary);
  transition: all 0.2s;
}

.defects-table .view-btn:hover {
  background: var(--primary);
  color: white;
}

.no-defects {
  text-align: center;
  padding: 50px 20px;
  color: #888;
}

.no-defects svg {
  color: #34A853;
  margin-bottom: 15px;
}

.upload-btn {
  display: inline-flex;
  align-items: center;
  gap: 10px;
  background: linear-gradient(135deg, var(--primary), #1a3a5c);
  color: white;
  border: none;
  padding: 14px 30px;
  border-radius: 10px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s;
}

.upload-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 5px 20px rgba(11, 31, 58, 0.25);
}

/* Defect Modal */
.defect-modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(11, 31, 58, 0.85);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 20px;
  backdrop-filter: blur(5px);
}

.defect-modal {
  background: white;
  border-radius: 16px;
  max-width: 500px;
  width: 100%;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
}

.defect-modal .modal-close {
  position: absolute;
  top: 15px;
  right: 15px;
  background: #f5f5f5;
  border: none;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  font-size: 1.5rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #666;
  z-index: 10;
}

.defect-modal .modal-close:hover {
  background: #eee;
}

.defect-modal .modal-header {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 25px;
  border-bottom: 3px solid;
  background: #fafbfc;
}

.defect-modal .severity-dot {
  width: 14px;
  height: 14px;
  border-radius: 50%;
  flex-shrink: 0;
}

.defect-modal .modal-header h3 {
  margin: 0;
  color: var(--primary);
  font-size: 1.1rem;
  flex: 1;
}

.defect-modal .modal-type {
  display: block;
  font-size: 0.85rem;
  color: #888;
  font-weight: normal;
}

.defect-modal .modal-severity {
  color: white;
  padding: 5px 12px;
  border-radius: 15px;
  font-size: 0.75rem;
  font-weight: 600;
}

.defect-modal .modal-body {
  padding: 25px;
}

.defect-modal .modal-section {
  margin-bottom: 20px;
}

.defect-modal .modal-section h4 {
  margin: 0 0 8px 0;
  color: var(--primary);
  font-size: 0.9rem;
}

.defect-modal .modal-section p {
  margin: 0;
  color: #555;
  line-height: 1.6;
}

.defect-modal .modal-footer {
  padding-top: 20px;
  border-top: 1px solid #eee;
}

.defect-modal .cost-display {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.defect-modal .cost-label {
  color: #888;
  font-size: 0.9rem;
}

.defect-modal .cost-value {
  color: var(--secondary);
  font-size: 1.2rem;
  font-weight: 700;
}

@media (max-width: 768px) {
  .defects-table-wrapper {
    margin: 0 -15px;
  }

  .defects-table {
    font-size: 0.8rem;
  }

  .defects-table th,
  .defects-table td {
    padding: 10px 8px;
  }

  .defects-table .desc-cell {
    max-width: 120px;
  }

  .section-header-row {
    flex-direction: column;
    align-items: stretch;
  }

  .download-pdf-btn {
    justify-content: center;
  }
}

/* Detail Modal */
.detail-modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(11, 31, 58, 0.85);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 20px;
  backdrop-filter: blur(5px);
}

.detail-modal {
  background: white;
  border-radius: 20px;
  max-width: 550px;
  width: 100%;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
  box-shadow: 0 25px 80px rgba(0, 0, 0, 0.3);
}

.modal-close {
  position: absolute;
  top: 20px;
  right: 20px;
  background: rgba(255, 255, 255, 0.9);
  border: none;
  color: #333;
  cursor: pointer;
  padding: 8px;
  border-radius: 50%;
  z-index: 10;
  transition: all 0.3s ease;
}

.modal-close:hover {
  background: white;
  transform: rotate(90deg);
}

.modal-header-bar {
  padding: 30px;
  color: white;
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 20px;
}

.modal-header-bar h2 {
  margin: 0;
  font-size: 1.4rem;
  font-weight: 700;
}

.modal-severity {
  background: rgba(255, 255, 255, 0.25);
  padding: 6px 16px;
  border-radius: 20px;
  font-size: 0.85rem;
  font-weight: 700;
  white-space: nowrap;
}

.modal-body-content {
  padding: 30px;
}

.info-block {
  margin-bottom: 25px;
}

.info-block h4 {
  color: #0B1F3A;
  font-size: 0.9rem;
  font-weight: 700;
  margin: 0 0 10px 0;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.info-block p {
  color: #555;
  line-height: 1.7;
  margin: 0;
}

.info-block.recommendation {
  background: linear-gradient(135deg, rgba(52, 168, 83, 0.1) 0%, rgba(52, 168, 83, 0.05) 100%);
  padding: 20px;
  border-radius: 12px;
  border-left: 4px solid #34A853;
}

.recommendation-content {
  display: flex;
  gap: 12px;
  align-items: flex-start;
}

.recommendation-content svg {
  color: #34A853;
  flex-shrink: 0;
  margin-top: 2px;
}

.cost-block {
  margin-bottom: 25px;
}

.cost-block h4 {
  color: #0B1F3A;
  font-size: 0.9rem;
  font-weight: 700;
  margin: 0 0 10px 0;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.cost-amount {
  background: linear-gradient(135deg, #C89D2A, #d4af37);
  color: white;
  padding: 20px 25px;
  border-radius: 12px;
  display: flex;
  align-items: baseline;
  gap: 10px;
}

.cost-amount .amount {
  font-size: 2rem;
  font-weight: 800;
}

.cost-amount .currency {
  font-size: 1rem;
  opacity: 0.9;
}

.modal-actions {
  display: flex;
  gap: 12px;
  margin-top: 25px;
}

.action-btn {
  flex: 1;
  padding: 14px 20px;
  border-radius: 10px;
  text-decoration: none;
  font-weight: 700;
  text-align: center;
  transition: all 0.3s ease;
  font-size: 0.95rem;
}

.action-btn.primary {
  background: linear-gradient(135deg, #34A853, #2e8b47);
  color: white;
}

.action-btn.primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(52, 168, 83, 0.3);
}

.action-btn.secondary {
  background: #f0f4f8;
  color: #0B1F3A;
  border: 2px solid #e0e5eb;
}

.action-btn.secondary:hover {
  border-color: #0B1F3A;
  background: #e8eef5;
}

/* Responsive Design */
@media (max-width: 768px) {
  .report-hero {
    padding: 40px 15px;
  }

  .report-hero h1 {
    font-size: 1.8rem;
  }

  .viewer-toolbar {
    flex-direction: column;
    gap: 15px;
    text-align: center;
  }

  .viewer-canvas {
    height: 350px;
  }

  .defects-grid {
    grid-template-columns: 1fr;
  }

  .section-header {
    flex-direction: column;
    gap: 15px;
    text-align: center;
  }

  .legend-grid {
    flex-direction: column;
    align-items: center;
  }

  .modal-actions {
    flex-direction: column;
  }

  .modal-header-bar {
    flex-direction: column;
    text-align: center;
    gap: 15px;
  }

  .car-badge {
    flex-direction: column;
    gap: 8px;
  }
}

/* ============================================
   UPLOAD & ANALYSIS SECTION
   ============================================ */
.upload-analysis-section {
  padding: 40px 20px;
  margin-top: -20px;
}

.upload-card {
  background: white;
  border-radius: 20px;
  padding: 40px;
  text-align: center;
  box-shadow: 0 10px 40px rgba(11, 31, 58, 0.08);
  max-width: 600px;
  margin: 0 auto 30px;
  border: 2px dashed #e0e5eb;
  transition: all 0.3s ease;
}

.upload-card:hover {
  border-color: #C89D2A;
  box-shadow: 0 15px 50px rgba(11, 31, 58, 0.12);
}

.upload-icon-wrapper {
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, rgba(200, 157, 42, 0.1), rgba(200, 157, 42, 0.05));
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 20px;
}

.upload-icon-wrapper svg {
  color: #C89D2A;
}

.upload-card h3 {
  color: #0B1F3A;
  font-size: 1.4rem;
  margin: 0 0 10px;
  font-weight: 700;
}

.upload-card p {
  color: #666;
  margin: 0 0 25px;
}

.upload-btn-main {
  display: inline-flex;
  align-items: center;
  gap: 10px;
  background: linear-gradient(135deg, #C89D2A, #d4af37);
  color: white;
  padding: 14px 35px;
  border-radius: 30px;
  font-weight: 700;
  font-size: 1rem;
  border: none;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 20px rgba(200, 157, 42, 0.3);
}

.upload-btn-main:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 8px 30px rgba(200, 157, 42, 0.4);
}

.upload-btn-main:disabled {
  opacity: 0.8;
  cursor: not-allowed;
}

.upload-btn-main .spin {
  animation: spin 1s linear infinite;
}

.error-message {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  color: #EA4335;
  margin-top: 15px;
  font-size: 0.9rem;
}

/* Analysis Progress */
.analysis-progress {
  background: white;
  border-radius: 20px;
  padding: 40px;
  max-width: 600px;
  margin: 0 auto;
  box-shadow: 0 10px 40px rgba(11, 31, 58, 0.08);
}

.analysis-progress h4 {
  color: #0B1F3A;
  text-align: center;
  margin: 0 0 30px;
  font-size: 1.2rem;
}

.progress-steps {
  display: flex;
  flex-direction: column;
  gap: 15px;
  margin-bottom: 25px;
}

.progress-step {
  display: flex;
  align-items: center;
  gap: 15px;
  padding: 12px 20px;
  background: #f8fafb;
  border-radius: 12px;
  opacity: 0.5;
  transition: all 0.3s ease;
}

.progress-step.active {
  opacity: 1;
  background: #f0f8f0;
}

.progress-step.current {
  background: linear-gradient(135deg, rgba(200, 157, 42, 0.1), rgba(200, 157, 42, 0.05));
  border: 1px solid rgba(200, 157, 42, 0.3);
}

.step-icon {
  width: 32px;
  height: 32px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.step-icon svg {
  color: #34A853;
}

.step-icon .spin {
  color: #C89D2A;
  animation: spin 1s linear infinite;
}

.step-number {
  width: 24px;
  height: 24px;
  background: #ddd;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.8rem;
  font-weight: 700;
  color: #888;
}

.progress-step.active .step-number {
  background: #34A853;
  color: white;
}

.step-text {
  font-weight: 600;
  color: #333;
}

.progress-bar {
  height: 6px;
  background: #e0e5eb;
  border-radius: 3px;
  overflow: hidden;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(135deg, #34A853, #4285F4);
  border-radius: 3px;
  transition: width 0.5s ease;
}

/* Analysis Result */
.analysis-result {
  background: white;
  border-radius: 20px;
  padding: 30px;
  max-width: 800px;
  margin: 30px auto 0;
  box-shadow: 0 10px 40px rgba(11, 31, 58, 0.08);
  border: 2px solid rgba(52, 168, 83, 0.2);
}

.result-header {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 20px;
  padding-bottom: 15px;
  border-bottom: 2px solid #f0f4f8;
}

.result-header svg {
  color: #34A853;
}

.result-header h3 {
  color: #0B1F3A;
  margin: 0;
  font-size: 1.3rem;
}

.result-content {
  background: #f8fafb;
  border-radius: 12px;
  padding: 25px;
  max-height: 500px;
  overflow-y: auto;
}

.result-content pre {
  white-space: pre-wrap;
  word-wrap: break-word;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  font-size: 0.95rem;
  line-height: 1.8;
  color: #333;
  margin: 0;
}

.close-result-btn {
  display: block;
  width: 100%;
  margin-top: 20px;
  padding: 14px;
  background: #f0f4f8;
  border: 2px solid #e0e5eb;
  border-radius: 10px;
  color: #0B1F3A;
  font-weight: 700;
  cursor: pointer;
  transition: all 0.3s ease;
}

.close-result-btn:hover {
  border-color: #0B1F3A;
  background: #e8eef5;
}

/* Car Visual Section */
.car-visual-section {
  padding: 40px 20px;
}

.section-header-center {
  text-align: center;
  margin-bottom: 30px;
}

.section-header-center h2 {
  color: #0B1F3A;
  font-size: 1.8rem;
  margin: 0;
}

.car-display-card {
  background: white;
  border-radius: 20px;
  padding: 30px;
  max-width: 800px;
  margin: 0 auto;
  box-shadow: 0 10px 40px rgba(11, 31, 58, 0.08);
}

.car-image-container {
  position: relative;
  width: 100%;
  padding-top: 50%;
  border-radius: 15px;
  overflow: hidden;
  background: linear-gradient(180deg, #f0f4f8 0%, #e8eef5 100%);
}

.realistic-car-image {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  max-width: 90%;
  max-height: 90%;
  object-fit: contain;
}

.defect-markers {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
}

.defect-marker {
  position: absolute;
  transform: translate(-50%, -50%);
  width: 32px;
  height: 32px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 700;
  font-size: 0.9rem;
  cursor: pointer;
  animation: pulse-marker 2s ease-in-out infinite;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
  z-index: 10;
}

.defect-marker.severity-high {
  background: #EA4335;
}

.defect-marker.severity-medium {
  background: #FFA500;
}

.defect-marker.severity-low {
  background: #4285F4;
}

@keyframes pulse-marker {
  0%, 100% { transform: translate(-50%, -50%) scale(1); }
  50% { transform: translate(-50%, -50%) scale(1.15); }
}

.car-info-badge {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  margin-top: 20px;
  padding: 12px 25px;
  background: linear-gradient(135deg, #0B1F3A, #1a3a5c);
  border-radius: 30px;
  color: white;
  font-weight: 600;
}

.car-info-badge svg {
  color: #C89D2A;
}

.year-badge {
  background: rgba(200, 157, 42, 0.3);
  padding: 4px 12px;
  border-radius: 15px;
  font-size: 0.85rem;
}

/* Hero Subtitle */
.hero-subtitle {
  font-size: 1.1rem;
  opacity: 0.9;
  margin: 0;
}

